declare module acme.sales.typedefs{   export interface Customer extends breeze.Entity {      Id: number;      Name: string;      Orders: Order[];   }   export interface Order extends breeze.Entity {      Id: number;      CustomerId: number;      OrderDate: Date;      Amount: number;      DueDate: Date;      PaidDate: Date;      Description: string;      Customer: Customer;      Deliveries: Delivery[];   }   export interface Delivery extends breeze.Entity {      Id: number;      OrderId: number;      Description: string;      PlannedDate: Date;      DeliveredDate: Date;      Order: Order;   }}